#!/usr/bin/env python
# encoding: utf-8
try:
    # python2
    from urlparse import urlparse
except ImportError:
    # python3
    from urllib.parse import urlparse


def parse_url(url):
    url = urlparse(url)
    assert url.scheme == 'consul' or not url.scheme
    host = url.hostname
    port = int(url.port or 8500)
    return (host, port)
