#!/usr/bin/env python
# -*- coding: utf-8 -*-
import unittest

from cfbclient.utils import DotDict


class TestCfbClient(unittest.TestCase):

    def test_dot_dict(self):
        girl = DotDict({'eye': {'color': 'blue', 'size': 'N'},
                        'bra': {'size': 'F'},
                        'la.la': {'who': 'you'}
                        })
        self.assertEqual(girl.eye.color, 'blue')
        self.assertEqual(girl.bra['size'], 'F')
        self.assertEqual(girl['la.la'], {'who': 'you'})
        self.assertEqual(girl['la.la'].who, 'you')
