# coding=utf8

from tracing import Statsd
from tracing.sampler import BaseSampler
from tracing.sender import Sender
from tracing.tracer import ZipkinTracer


class Initializer(object):

    def __init__(self, unit_name="stub-unit", statsd_host="status", **kwargs):
        self.unit_name = unit_name
        self.statsd_host = statsd_host

    @property
    def tracer(self):
        Statsd.initialize("stub-app", self.unit_name, self.statsd_host, 8126)
        return ZipkinTracer(self.unit_name, "stub-version",
                            BaseSampler(1), Sender())


