#!/usr/bin/env python
# encoding: utf-8


class TZoneException(Exception):
    pass


class TransportException(TZoneException):
    pass


class TimeoutError(TZoneException):
    pass


class ConnectionError(TZoneException):
    pass


class RemoteServerError(TZoneException):

    def __init__(self, code, message, *args, **kwargs):
        self.code = code
        self.message = message

    def __str__(self):
        message = "HTTP %d: %s" % (self.code, self.message)
        return message
