#!/usr/bin/env python
# encoding: utf-8
from thrift.protocol.TBinaryProtocol import TBinaryProtocolAccelerated

_old_writeString = TBinaryProtocolAccelerated.writeString


def writeString(self, s):
    if type(s) is unicode:
        s = s.encode("utf8")
    _old_writeString(self, s)

TBinaryProtocolAccelerated.writeString = writeString
